import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const styles = theme => ({
  root: {
    width: '100%',
    marginTop: theme.spacing.unit * 3,
    overflowX: 'auto',
  },
  table: {
    minWidth: 700,
  },
});

let id = 0;
function createData(name, calories, fat, carbs, protein) {
  id += 1;
  return { id, name, calories, fat, carbs, protein };
}

const rows = [
  createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
  createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
  createData('Eclair', 262, 16.0, 24, 6.0),
  createData('Cupcake', 305, 3.7, 67, 4.3),
  createData('Gingerbread', 356, 16.0, 49, 3.9),
];

let rows2 = [ 'hi is this', 'is is ', 'third one ' ];

// let sentence_test= [ { word: 'is', sentence: 'hi this ceroth sentence' },
//     { word: 'is', sentence: ' is car is car this sentence one' },
//     { word: 'car', sentence: ' is car is car' },
//     { word: 'is', sentence: ' is iguana' } ]

function SimpleTable(props) {
  const { classes , sentence_test , word} = props;

  console.log("*This is sentence_test.sentence:", sentence_test[0].sentence, word);

  /// input data and word and returs array with the sentences connected to that word
function create_table_data(data, word_required){

  let sentences = [];
  const result = data.filter(word => word.word == word_required);

  for(let i=0;i<result.length;i++){
    sentences.push(result[i].sentence)
  }

  return sentences
}

let sentences_in_table = create_table_data(sentence_test,"is"); 
//TODO MAP this array 
//and inset it in table !!
console.log("SENTENCES IN TABLE", sentences_in_table);

  return (
    <Paper className={classes.root}>
      <Table className={classes.table}>

        <TableBody>
          {rows.map(row => (
            <TableRow key={row.id}>
              <TableCell component="th" scope="row">
                {row.name} 
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </Paper>
  );
}

SimpleTable.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SimpleTable);
