import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import SimpleTable from './SimpleTable';

function TabContainer(props) {
  return (
    <Typography component="div" style={{ padding: 8 * 3 }}>
      {props.children}
    </Typography>
  );
}

TabContainer.propTypes = {
  children: PropTypes.node.isRequired,
};

const styles = theme => ({
  root: {
    flexGrow: 1,
    width: '100%',
    backgroundColor: theme.palette.background.paper,
  },
});

// function createData(name, calories, fat, carbs, protein) {
//     id += 1;
//     return { id, name, calories, fat, carbs, protein };
//   }

//   const rows = [
//     createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
//     createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
//     createData('Eclair', 262, 16.0, 24, 6.0),
//     createData('Cupcake', 305, 3.7, 67, 4.3),
//     createData('Gingerbread', 356, 16.0, 49, 3.9),
//   ];

let sentence_test= [ { word: 'is', sentence: 'hi this ceroth sentence' },
    { word: 'is', sentence: ' is car is car this sentence one' },
    { word: 'car', sentence: ' is car is car' },
    { word: 'is', sentence: ' is iguana' } ]

        // <TableBody>
        //   {rows.map(row => (
        //     <TableRow key={row.id}>
        //       <TableCell component="th" scope="row">
        //         {row.name}
        //       </TableCell>
        //       <TableCell align="right">{row.calories}</TableCell>
        //       <TableCell align="right">{row.fat}</TableCell>
        //       <TableCell align="right">{row.carbs}</TableCell>
        //       <TableCell align="right">{row.protein}</TableCell>
        //     </TableRow>
        //   ))}
        // </TableBody>

class ScrollableTabsButtonAuto extends React.Component {
  state = {
    value: 0
  };

  handleChange = (event, value) => {
    this.setState({ value });
  };


  render() {
    let top_words_sorted = null;
    {top_words_sorted = (this.props.top_words_sorted || ["one", "two", "three"])};
    //{top_words_sorted = top_words_sorted.shift()}
    const { classes } = this.props;
    const { value } = this.state;
    console.log('**** top_words_sorted: ', this.props.top_words_sorted);
    return (
      <div className={classes.root}>
        <AppBar position="static" color="default">
          <Tabs
            value={value}
            onChange={this.handleChange}
            indicatorColor="primary"
            textColor="primary"
            variant="scrollable"
            scrollButtons="auto"
          >
            {top_words_sorted.map(label => (
            <Tab key={label} value={label} label={label} />
            ))}
            {/* <Tab label="Item One" />
            <Tab label="Item Two" />
            <Tab label="Item Eight" /> */}
          </Tabs>
        </AppBar>
        {this.state.value === 0 && <TabContainer> <SimpleTable sentence_test={sentence_test[0]}/> </TabContainer>}
        {value === 1 && <TabContainer> <SimpleTable sentence_test={sentence_test[1]}/> </TabContainer>}
        {value === 2 && <TabContainer>item three</TabContainer>}
        {value === 3 && <TabContainer>Item Four</TabContainer>}
        {value === 4 && <TabContainer>Item Five</TabContainer>}
        {value === 5 && <TabContainer>Item Six</TabContainer>}
        {value === 6 && <TabContainer>Item Seven</TabContainer>}
      </div>
    );
  }
}

ScrollableTabsButtonAuto.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ScrollableTabsButtonAuto);
